import React, {useState} from 'react';
import DatePicker from "react-datepicker"; 
import "react-datepicker/dist/react-datepicker.css";

export default function CreateEvents() {
    const [title,setTitle] = useState("");
    const [description,setDescription] = useState("");
    const [location,setLocation] = useState("");
    const [skills,setSkills] = useState([]);
    const [time,setTime] = useState(new Date());

    return (
        <div>
            <h1>Create Event</h1>
            <form>
                <div className="form-group">
                    <label for="title">Title</label>
                    <input type="text" className="form-control" id="title" aria-describedby="titleDescription" placeholder="Enter title" value={title} onChange={setTitle} />
                    <small id="titleDescription" className="form-text text-muted">What's the title of your event?</small>
                </div>
                <div className="form-group">
                    <label for="description">Description</label>
                    <input type="text" className="form-control" id="description" aria-describedby="descriptionDescription" placeholder="Enter description" value={description} onChange={setDescription} />
                    <small id="descriptionDescription" className="form-text text-muted">What's the description of your event?</small>
                </div>
                <div className="form-group">
                    <label for="location">Location</label>
                    <input type="text" className="form-control" id="location" aria-describedby="locationDescription" placeholder="Enter location" value={location} onChange={setLocation} />
                    <small id="locationDescription" className="form-text text-muted">What's the location of your event?</small>
                </div>
                <div className="form-group">
                    <label for="skills">Skills</label>
                    <input type="text" className="form-control" id="skills" aria-describedby="skillsDescription" placeholder="Enter skills" value={skills} onChange={setSkills} />
                    <small id="skillsDescription" className="form-text text-muted">What's the skills of your event?</small>
                </div>
                <div className="form-group">
                    <DatePicker
                        className="form-control"
                        selected={time}
                        onChange={setTime}
                    />
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>           
        </div>
    )
}
