import React from "react";
import "./event-card.css";

const EventCard = props => {
  const { event } = props;
  return (
    <div className="card">
      <div className="card-body">
        <div className="row">
          <div className="col-sm-3">
            <img src="https://picsum.photos/200/300" alt="text" />
          </div>
          <div className="col-sm-9">
            <h3>{event.title}</h3>
            <p>{event.description}</p>
            <p>
              <img
                className="location-icon"
                src="/imgs/calendar.png"
                alt="calendar-icon"
              />
              {event.time.toLocaleDateString({
                weekday: "long",
                year: "numeric",
                month: "long",
                day: "numeric"
              })}
            </p>
            <p>
              <img
                className="location-icon"
                src="/imgs/location.png"
                alt="location-icon"
              />
              {event.location}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventCard;
