import React, {useState} from 'react';
import DatePicker from "react-datepicker"; 
import "react-datepicker/dist/react-datepicker.css";

export default function CreateEvents() {
    const [title,setTitle] = useState("");
    const [description,setDescription] = useState("");
    const [location,setLocation] = useState("");
    const [time,setTime] = useState("");

    return (
        <div>
            <form>
                <div class="form-group">
                    <label for="title">Title</label>
                    <input type="text" class="form-control" id="title" aria-describedby="titleDescription" placeholder="Enter title" value={title} onChange={setTitle} />
                    <small id="titleDescription" class="form-text text-muted">What's the title of your event?</small>
                </div>
                <div class="form-group">
                    <label for="description">Title</label>
                    <input type="text" class="form-control" id="description" aria-describedby="descriptionDescription" placeholder="Enter description" value={description} onChange={setDescription} />
                    <small id="descriptionDescription" class="form-text text-muted">What's the description of your event?</small>
                </div>
                <div class="form-group">
                    <label for="location">location</label>
                    <input type="text" class="form-control" id="location" aria-describedby="locationDescription" placeholder="Enter location" value={location} onChange={setLocation} />
                    <small id="locationDescription" class="form-text text-muted">What's the location of your event?</small>
                </div>
                <div class="form-group">
                    <label for="title">Title</label>
                    <input type="text" class="form-control" id="title" aria-describedby="titleDescription" placeholder="Enter title" value={title} onChange={setTitle} />
                    <small id="titleDescription" class="form-text text-muted">What's the title of your event?</small>
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>           
        </div>
    )
}
