import React, {useState} from 'react';
import DatePicker from "react-datepicker"; 
import "react-datepicker/dist/react-datepicker.css";

export default function CreateEvents() {
    const [title,setTitle] = useState("");
    const [description,setDescription] = useState("");
    const [location,setLocation] = useState("");
    const [skills,setSkills] = useState([]);
    const [newSkill,setNewSkill] = useState("");
    const [time,setTime] = useState(new Date());

    const skillsHandler  = (e) => {

    }

    return (
        <div>
            <h1>Create Event</h1>
            <form>
                <div className="form-group">
                    <label for="title">Title</label>
                    <input type="text" className="form-control" id="title" aria-describedby="titleDescription" placeholder="Enter title" value={title} onChange={setTitle} />
                </div>
                <div className="form-group">
                    <label for="description">Description</label>
                    <input type="text" className="form-control" id="description" aria-describedby="descriptionDescription" placeholder="Enter description" value={description} onChange={setDescription} />
                </div>
                <div className="form-group">
                    <label for="location">Location</label>
                    <input type="text" className="form-control" id="location" aria-describedby="locationDescription" placeholder="Enter location" value={location} onChange={setLocation} />
                </div>
                <div className="form-group">
                    <label for="skills">Skills Needed</label>
                    <div className="input-group">
                        <div className="input-group-prepend">
                            <button className="btn btn-outline-secondary" onClick={skillsHandler} type="button">Add Skill</button>
                        </div>
                        <input type="text" className="form-control" id="skills" aria-describedby="skillsDescription" placeholder="Enter a skill needed" value={newSkill} onChange={setNewSkill} />
                    </div>
                    <small id="skillsDescription" className="form-text text-muted">What skills do you need for your event?</small>
                    {skills.map((v,i) => <span key={i} className="badge badge-pill badge-primary">{v}</span>)}
                </div>
                <div className="form-group">
                    <label for="date">Date Of Event</label><br/>
                    <DatePicker
                        id="date"
                        className="form-control"
                        selected={time}
                        onChange={setTime}
                    />
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>           
        </div>
    )
}
