import React, { useState, useEffect } from "react";
import EventCard from "../event-card/event-card";
import Filter from "../filter/filter";
import { getEvents } from "../../services/event-service";
import "./events.css";

const Events = props => {
  const [events, setEvents] = useState(getEvents());
  const [skills, setSkills] = useState([
    { id: 8, name: "Coding" },
    { id: 9, name: "Developers" },
    { id: 10, name: "Heavy Lifting" },
    { id: 11, name: "any" },
    { id: 12, name: "Coaching" },
    { id: 13, name: "Critter Wrangling" },
    { id: 14, name: "Tutoring" },
    { id: 15, name: "Tutoring math" },
    { id: 16, name: "Power Tools" },
    { id: 17, name: "Dog Walking" }
  ]);

  useEffect(() => {
    const selectedSkills = skillsCopy
      .filter(s => s.active)
      .map(s => s.name)
      .join("-");

    console.log("selectedSkills", selectedSkills);
    const matchingEvents = events.filter(
      e => e.skills.filter(s => selectedSkills.includes(s.name)).length > 0
    );

    const test = events[0].skills.filter(s => selectedSkills.includes(s.name));
    console.log("test", test);

    setEvents(matchingEvents);
  }, [skills]);

  const handleAttendEvent = e => {
    console.log(e.currentTarget.id);
  };

  const handleFilterEvent = e => {
    const name = e.currentTarget.id;
    const skillsCopy = [...skills];
    const index = skillsCopy.findIndex(s => s.name === name);
    skillsCopy[index].active = !skillsCopy[index].active;
    setSkills(skillsCopy);
  };

  return (
    <React.Fragment>
      <Filter filterEventHandler={handleFilterEvent} skills={skills} />
      <div className="event-container">
        {events.map(e => (
          <EventCard
            key={e.id}
            event={e}
            attendEventHandler={handleAttendEvent}
          />
        ))}
      </div>
    </React.Fragment>
  );
};

export default Events;
