import React, { useState } from "react";
import OrganizationForm from "./organizationForm";
import VolunteerForm from "./volunteerForm";
import { createOrganization } from "../../services/organization-service";

export default function Register() {
  const [registerAs, setRegisterAs] = useState(null);

  const handleCreateOrganization = e => {
    e.preventDefault();
    const organization = {
      name,
      description,
      website,
      facebook,
      twitter,
      instagram,
      email,
      phone,
      password
    };
    createOrganization(organization);
    props.history.push("/events");
  };
  return (
    <div>
      <div class="text-center" style={{ marginBottom: "50px" }}>
        <button
          className="btn btn-primary btn-lg p-2 m-2"
          onClick={() => setRegisterAs("organization")}
        >
          Register As An Organization
        </button>
        <button
          className="btn btn-secondary btn-lg p-2 m-2"
          onClick={() => setRegisterAs("volunteer")}
        >
          Register As A Volunteer
        </button>
      </div>
      {registerAs === "organization" && (
        <OrganizationForm onCreateOrganization={handleCreateOrganization} />
      )}
      {registerAs === "volunteer" && <VolunteerForm />}
    </div>
  );
}
